import React, { Component } from 'react'
import "leaflet/dist/leaflet.css";
import NavBar from '../../NavBar/Navbar';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import Button from '@mui/material/Button';
import icon from 'leaflet/dist/images/marker.png';
import L from 'leaflet';
import ChangeView from './ChangeView';
import {Card, Table, ButtonGroup, Form, Row} from 'react-bootstrap';
import "./container.css";
import "./leaflet.css";

let DefaultIcon = L.icon({
    iconUrl: icon,
    iconSize: [32,32]
});

L.Marker.prototype.options.icon = DefaultIcon;

export default class map extends Component {

  constructor(props){
    super(props)

      this.state = {
          zoomLevel: 10,
          entreprise: '',
          lat: '34.022',
          lang: '-6.83762',
          entreprises : []
      }
  }



  render() {
    return (
      <>
        <div className="app">
          <div className="app__body">
            <div className="header">
              <div className="header__bar">
                <div className="title">
                  Recherche des Entreprises
                </div>
                <Form style={{marginLeft:'110px'}}>
                  <Row>
                    <Form.Label>Ville</Form.Label>
                    <Form.Select aria-label="Default select example" style={{marginRight:'100px'}}>
                      <option value='fes'>
                        fes
                      </option>
                    </Form.Select>
                  </Row>
                </Form>
                <Form style={{marginRight:'120px'}}>
                  <Row>
                    <Form.Label>Secteur</Form.Label>
                    <Form.Select aria-label="Default select example" style={{marginRight:'70px'}}>
                      <option value='rabat'>
                        rabat
                      </option>
                    </Form.Select>
                  </Row>
                </Form>
              </div>
              <div className="map__body">
                <MapContainer style={{height: '422px', width:'210vh'}} center={[this.state.lat, this.state.lang]} zoom={this.state.zoomLevel} scrollWheelZoom={false}>
                  <ChangeView center={[this.state.lat, this.state.lang]} zoom={this.state.zoomLevel} />
                  <TileLayer
                    attribution='<a href="https://www.maptiler.com/copyright/" target="_blank">&copy; MapTiler</a> <a href="https://www.openstreetmap.org/copyright" target="_blank">&copy; OpenStreetMap contributors</a>'
                    url="https://api.maptiler.com/maps/streets/256/{z}/{x}/{y}.png?key=dumwj19cUL35SI5Gnayy"
                  />
                  <Marker position={[this.state.lat, this.state.lang]}>
                    <Popup>
                      <h1>its a popup</h1>
                    </Popup>
                  </Marker>
                </MapContainer>
              </div>
            </div>
          </div>
        </div>
      </>
    )
  }
}
