import React, { Component } from 'react';
import EntrepriseService from '../../../services/EntrepriseServices.js';
import '../AddEntreprise/add.css';
import {Card, Table, ButtonGroup} from 'react-bootstrap';
import Button from '@mui/material/Button';


export default class listEntreprise extends Component {
    constructor(props){
        super(props)

        this.state = {
            entreprise : []
        }
    }

    componentDidMount(){
        EntrepriseService.getEntreprises()
            .then((res) => {
                this.setState({entreprise: res.data});
            })
            .catch((err) => console.log('error : ' + err));
    }

    render() {
        return (
        <>
            
                <Card style={{ width: '70rem' }} className="container2 border border-dark bg-dark text-white">
                    <Card.Header>
                            <span style={{marginRight:'5px'}}></span> Entreprise List
                            <Button variant="contained" color="primary"  style={{marginLeft:'820px'}}>Add Entreprise</Button>
                    </Card.Header>

                    <Card.Body>
                        <Table striped bordered hover variant="dark">
                            <thead>
                                <tr>
                                    <th>Name</th>
                                    <th>Secteur</th>
                                    <th>Ville</th>
                                    <th>Address</th>
                                    <th>Tel</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.entreprise.length === 0 ?
                                        <tr align="center">
                                            <td colSpan="5"> No Entreprise Available </td>
                                        </tr>
                                    :
                                    this.state.entreprise.map(entr => 
                                        <tr key={entr.id}>
                                            <td>{entr.name}</td>
                                            <td>{entr.secteur}</td>
                                            <td>{entr.ville}</td>
                                            <td>{entr.address}</td>
                                            <td>{entr.tel}</td>
                                            <td>
                                                <ButtonGroup>
                                                    <Button variant="contained" color="primary" style={{height: '38px', fontSize:'15px', marginRight:'20px'}}>Edit</Button>{' '}
                                                    <Button variant="contained" color="secondary" style={{height: '38px', fontSize:'15px', marginRight:'-150px'}}>delete</Button>
                                                </ButtonGroup>
                                            </td>
                                        </tr>
                                    )
                                }
                            </tbody>
                        </Table>
                    </Card.Body>
                </Card>
            
        </>
        )
    }
}
